##----------------------------------------------------------------------------
##
## Title-
##       ~/src/py/site-packages/.setup.sh
##
## Purpose-
##       If version is out of date, update the package
##
## Last change date-
##       2024/03/01
##
##############################################################################

##############################################################################
## Parameter and environment verification
[[ -z "$1" ]] && { echo ".setup.sh: Missing parameter" ; exit 1 ; }

if [[ -z "$SDL_ROOT" ]] ; then
   ## Parse the parameter. For each xx in xx/xx/xx create ../../..
   for path in ${1//\// } ; do
     if [[ -n "$SDL_ROOT" ]] ; then
       SDL_ROOT=$SDL_ROOT/..
     else
       SDL_ROOT=..
     fi
   done
   export SDL_ROOT=$SDL_ROOT
fi

##############################################################################
## If we are up to date, do nothing
want=`"$SDL_ROOT/bat/sys/rdconfig" "$SDL_ROOT/bat/sys/.want-version" "$1"`
if [[ -r "./.have-version" ]] ; then
   have=`$SDL_ROOT/bat/sys/rdconfig ./.have-version "$1"`
   [[ "$want" == "$have" ]] && exit 0

   echo ".setup.sh: .have $have, .want $want"
fi

##############################################################################
## We are not up to date: rebuild
rm -f ./.have-version
python -m pip install .
$SDL_ROOT/bat/sys/wrconfig ./.have-version "$1" "$want"
exit 0
