##
## Purpose-
##       Install stdio module.
##
## Last change date-
##       2023/08/12
##
## Usage-
##       setup.py build: Create the stdio module
##       setup.py sdist: Create distribution information
##
##############################################################################
from distutils.core import setup, Extension

module1 = Extension('stdio',
                    include_dirs = ['/usr/local/include', '/usr/include'],
                    library_dirs = ['/usr/local/lib', '/usr/lib'],
                    sources = ['stdiomodule.c'])

setup(name='stdio',
      version='1.0.1',
      author='Frank Eskesen',
      description='Does single char input/output, like C getchar/putchar',
      long_description='''The stdio module does single-character input/output
by providing wrappers for the stdio.h library functions
getchar() and putchar().
''',
      ext_modules=[module1])
